mapper.select:


    ss.order_num like
        concat(concat('%',#{ss.orderNum}),'%')

    or ss.shipments_num like
        concat(concat('%',#{ss.shipmentsNum}),'%')

    or ss.clientele_num like
        concat(concat('%',#{ss.clienteleNum}),'%')

    or ss.clientele_name like
        concat(concat('%',#{ss.clienteleName}),'%')


        and ss.status=#{ss.status}


        and ss.shipments_status=#{ss.shipmentsStatus}


        and ss.outbound_status=#{ss.outboundStatus}

    ${ss.params.dataScope}

order by ss.create_time desc

ss.order_num like concat(concat('%',#{ss.orderNum}),'%')
or ss.shipments_num like concat(concat('%',#{ss.shipmentsNum}),'%')
or ss.clientele_num like concat(concat('%',#{ss.clienteleNum}),'%')
or ss.clientele_name like concat(concat('%',#{ss.clienteleName}),'%')
and ss.status=#{ss.status}
and ss.shipments_status=#{ss.shipmentsStatus}
and ss.outbound_status=#{ss.outboundStatus}
order by ss.outbound_status ,ss.create_time desc
