mapper.select:
select sign.create_by, sign.create_time, sign.update_by, sign.update_time, sign.signback_id, sign.order_id,sign.signback_status,sign.order_time, sign.shipments_id, sign.signback_num, sign.shipments_num, sign.order_num, sign.signback_time, sign.clientele_id, sign.clientele_num, sign.clientele_name, sign.personnel_id, sign.personnel_name, sign.status, sign.leader, sign.email, sign.phone, sign.mobilephone, sign.address, sign.audit_by, sign.audit_time, sign.remark,sign_num,rejection_num,process_mode ,u.nickname as personnel_name from sys_signback sign left join sys_user u on u.user_id = sign.personnel_id /*left join sys_dept d on d.dept_id = u.dept_id*/
sign.signback_num like concat(concat('%',#{sign.signbackNum}),'%')
or sign.order_num like concat(concat('%',#{sign.orderNum}),'%')
or sign.shipments_num like concat(concat('%',#{sign.shipmentsNum}),'%')
or sign.clientele_num like concat(concat('%',#{sign.clienteleNum}),'%')
or sign.clientele_name like concat(concat('%',#{sign.clienteleName}),'%')
and sign.clientele_id=#{sign.clienteleId}
and sign.status=#{sign.status}
${sign.params.dataScope}
order by sign.create_time desc
